import axios from "axios";
import setAuthToken from "../../utils/setAuthnToken";
import { LIKE_SONG, ALL_SONG } from "../types";

export const userFavSong = () => async (dispatch) => {
  setAuthToken(localStorage.token);

  const allSongs = await axios.get("/userchoice/allFavSongs");
  dispatch({
    type: ALL_SONG,
    payload: allSongs.data,
  });
};

export const likeSong = (songID) => async (dispatch) => {
  setAuthToken(localStorage.token);
  const config = {
    header: {
      "content-type": "application/json",
    },
  };
  const res = await axios.post(`/song/likeSong/${songID}`, null, config);
  const payload = {
    id: songID,
    data: res.data,
  };
  dispatch({
    type: LIKE_SONG,
    payload: payload,
  });
};
