import React, { Fragment, useEffect } from "react";
import { useSelector } from "react-redux";

const Songs = ({ songsList }) => {
  const [songFavouriteIconClass, setSongFavouriteIconClass] =
    useState("far fa-heart");

  const songFavourite = (e) => {
    dispatch(userChoiceAction.addFav("favrapper", currArtist._id));
    setSongFavouriteIconClass(
      songFavouriteIconClass === "far fa-heart"
        ? "fas fa-heart"
        : "far fa-heart"
    );
  };

  return (
    songsList !== null && (
      <div className="d-flex pb-3" style={{ color: "white" }}>
        {songsList.map((song) => (
          <div className="col-md-3" id={song._id}>
            <div>
              <a
                href={song.songlinks[0]["youtube"]}
                target="_blank"
                rel="noreferrer noopener"
              >
                <img src={song.img} className="border border-3 rounded"></img>
              </a>
            </div>
            <div>{song.name}</div>

            <div
              style={{ color: "#FFFFFF" }}
              className="d-flex justify-content-evenly"
            >
              <div>
                <i
                  onClick={() => artistLikedUnliked("like")}
                  className="fas fa-microphone"
                ></i>
                <span className="ps-3">{currArtist.like}</span>
              </div>
              <div>
                <i
                  onClick={songFavourite}
                  className={songFavouriteIconClass}
                ></i>
              </div>
              <div>
                <i
                  onClick={() => artistLikedUnliked("unLike")}
                  className="fas fa-microphone-slash"
                ></i>
                <span className="ps-3">{currArtist.unLike}</span>
              </div>
            </div>

            <div className="d-flex justify-content-evenly">
              <div className="pe-2">
                <i
                  className="fas fa-thumbs-up"
                  style={{ color: "#FFFFFF" }}
                ></i>
                <span className="ps-2">{song.like}</span>
              </div>
              <div>
                <i
                  className="fas fa-thumbs-down"
                  style={{ color: "#FFFFFF" }}
                ></i>
                <span className="ps-2" style={{ color: "#FFFFFF" }}>
                  {song.dislike}
                </span>
              </div>
            </div>
          </div>
        ))}
      </div>
    )
  );
};

export default Songs;
