import { LIKE_SONG, ALL_SONG } from "./../types";

const initialState = {
  name: null,
  like: null,
  dislike: null,
  rapper: null,
  beatproducer: null,
  songlinks: null,
};

export default (state = { initialState }, action) => {
  switch (action.type) {
    case LIKE_SONG:
      return {
        ...state,
        like: action.payload.like + 1,
      };
    case ALL_SONG:
      return { ...state, ...action.payload };
    default:
      return state;
  }
};
