import { SET_ALERT, REMOVE_ALERT } from "../types";

const initialState = [];
export default (state = { initialState }, action) => {
  switch (action.payload.type) {
    case SET_ALERT:
      return {
        ...state,
        ...action.payload,
      };

    case REMOVE_ALERT:
      return state.filter((alert) => alert.id != action.payload.id);

    default:
      return { ...state };
  }
};
