import React, { useEffect, useState } from "react";

const Rapper = ({ match }) => {
  const [rapperInfo, setRapperInfo] = useState([]);

  async function fetchArtistData(rapperName) {
    const res = await fetch(`http://localhost:5000/rapper/${rapperName}`);
    const data = await res.json();
    setRapperInfo(data);
  }
  useEffect(() => {
    try {
      fetchArtistData(match.props.rapper);
    } catch (error) {}
  }, [match.props.rapper]);

  return (
    <div className="container">
      <div className="row">
        <div className="col-2">
          <img
            src={
              require(`../../resources/artist/images/page/${profileImage}`)
                .default
            }
          />
        </div>
        <div className="col-10">
          <div>name box</div>
          <div>About box</div>
        </div>
      </div>
      <div className="row">
        <div className="col-8">SOngs famous</div>
        <div className="col-4">social links</div>
      </div>
    </div>
  );
};
export default Rapper;
