import React, { useState } from "react";
import profileImage from "../../resources/images/defaultProfile.png";
import { useSelector } from "react-redux";

const User = () => {
  const localUser = useSelector((state) => state.user.user);
  const [user, setUser] = useState(localUser);

  const onChange = (e) => {
    setUser({
      ...user,
      [e.target.name]: [e.target.value],
    });
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-md-9 border border-primary">user preference</div>
        <div className="col-md-3 border border-primary">
          <div className="row-cols-md-3 text-center">
            <img
              src={profileImage}
              alt="Profile image"
              style={{ width: "60%", height: "70%" }}
              className="border rounded-circle"
            />
            <div className="p-2">
              <form>
                <div className="mb-3">
                  <label forHtml="userName" className="form-label">
                    User Name
                  </label>
                  <input
                    type="text"
                    name="name"
                    id="userName"
                    value={user.name}
                    onChange={onChange}
                    className="form-control"
                  />
                </div>
                <div className="mb-3">
                  <label forHtml="userEmail" className="form-label">
                    User Email
                  </label>
                  <input
                    type="email"
                    name="email"
                    id="userEmail"
                    value={user.email}
                    onChange={onChange}
                    className="form-control"
                  />
                </div>
                <div className="mb-3">
                  <label forHtml="userPhone" className="form-label">
                    User Phone Number
                  </label>
                  <input
                    type="text"
                    name="phoneno"
                    id="userPhone"
                    value={user.email}
                    onChange={onChange}
                    className="form-control"
                  />
                </div>
              </form>
            </div>
            <div className="p-2">Flex item 3</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default User;
