import { SET_ALERT, REMOVE_ALERT } from "../types";

const initialState = {
  alerts: [],
};
export default (state = initialState, action) => {
  switch (action.type) {
    case SET_ALERT:
      return {
        ...state,
        alerts: [...state.alerts, action.payload],
      };

    case REMOVE_ALERT:
      return {
        ...state,
        alerts: state.alerts.filter((alert) =>
          console.log("alert is " + alert.msg + " and id is " + alert.id)
        ),
      };

    default:
      return { ...state };
  }
};
