import axios from "axios";
import setAuthToken from "../../utils/setAuthnToken";
import { LIKE_SONG, ALL_SONG } from "../types";

export const song = (allSongs) => async (dispatch) => {
  if (allSongs === null) {
    allSongs = await axios.get("/song/all");
    allSongs = allSongs.data;
  }
  dispatch({
    type: ALL_SONG,
    payload: allSongs,
  });
};

export const likeSong = (songID) => async (dispatch) => {
  setAuthToken(localStorage.token);
  const config = {
    header: {
      "content-type": "application/json",
    },
  };
  const res = await axios.post(`/song/likeSong/${songID}`, null, config);
  const payload = {
    id: songID,
    data: res.data,
  };
  dispatch({
    type: LIKE_SONG,
    payload: payload,
  });
};
