import axios from "axios";
import setAuthToken from "../../utils/setAuthnToken";
import { LIKE_SONG } from "../types";

export const song = () => () => async (dispatch) => {
  const allSongs = await axios.get("/song/all");
  dispatch({
    type: "ALL_SONG",
    payload: allSongs.data,
  });
};

export const likeSong = (songID) => async (dispatch) => {
  setAuthToken(localStorage.token);
  const config = {
    header: {
      "content-type": "application/json",
    },
  };
  const res = await axios.post(`/song/likeSong/${songID}`, null, config);
  dispatch({
    type: LIKE_SONG,
    payload: res.data,
  });
};
