import React, { useEffect, useState } from "react";

const Rapper = ({ match }) => {
  const [rapperInfo, setRapperInfo] = useState([]);
  const [loading, setLoading] = useState(false);
  async function fetchArtistData(rapperName) {
    const res = await fetch(
      `http://localhost:5000/rappersinfos?name=${rapperName}`
    );
    const data = await res.json();
    setRapperInfo(data);
    setLoading(!loading);
  }
  useEffect(() => {
    try {
      fetchArtistData(match.params.rapper);
    } catch (error) {}
  }, [match.params.rapper]);

  return (
    loading && (
      <div className="container">
        <div className="row">
          <div className="col-2 ">
            <img
              className="imageprofile"
              src={
                require(`../../../resources/artist/images/page/${rapperInfo[0].pageimage}`)
                  .default
              }
            />
          </div>
          <div className="col-10">
            <div>name box</div>
            <div>About box</div>
          </div>
        </div>
        <div className="row">
          <div className="col-8">SOngs famous</div>
          <div className="col-4">social links</div>
        </div>
      </div>
    )
  );
};
export default Rapper;
