import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";
import HorizontalScroll from "./../../common/HorizontalScroll";
import { actions } from "../../../state/actions";
const Rappers = () => {
  const artistType = "rappers";
  const dispatch = useDispatch();
  dispatch(actions.currentArtistType(artistType));
  const titles = ["OG", "Upcoming"];

  const [horizontalScrolls, setHorizontalScrolls] = useState([]);
  function fetchBeatProducerData() {
    try {
      dispatch(actions.artistsInfo(artistType, titles));
      const artistType = useSelector((state) => state.artist.artists);
      console.log("Start executing calls");
      axios
        .all(
          titles.map((title) =>
            axios.get(`/artist/${artistType}/title/${title}`)
          )
        )
        .then(
          axios.spread(function (...res) {
            // all requests are now complete
            console.log("final response is" + res);
            setHorizontalScrolls(res);
          })
        );
    } catch (error) {
      console.log("Error is " + error);
    }
  }
  useEffect(() => {
    try {
      fetchBeatProducerData();
    } catch (error) {
      console.log("error is " + error.message);
    }
  }, []);
  return (
    <div>
      {artistType !== null &&
        artistType.map((horizontalScroll, i) => (
          <HorizontalScroll
            key={i}
            horizontalScroll={horizontalScroll}
            title={titles[i]}
          ></HorizontalScroll>
        ))}
    </div>
  );
};
export default Rappers;
