import axios from "axios";
import { FAV_RAPPER, FAV_BEATPRODUCER } from "../types";
export const favRapper = (userId) => async (dispatch) => {
  const favRapperInfo = await axios.get(`/userchoice/${userId}/favrapper`);
  dispatch({
    type: FAV_RAPPER,
    payload: favRapperInfo,
  });
};

export const favBeatProducer = (userId) => async (dispatch) => {
  const beatProducerInfo = await axios.get(`/userchoice/${userId}/favrapper`);
  dispatch({
    type: FAV_BEATPRODUCER,
    payload: beatProducerInfo,
  });
};

export const favSongs = (userId) => async (dispatch) => {
  const favSongInfo = await axios.get(`/userchoice/${userId}/favsong`);
  dispatch({
    type: FAV_SONG,
    payload: favSongInfo,
  });
};
