import { SET_ALERT, REMOVE_ALERT } from "../types";

const initialState = [];
export default (state = [], action) => {
  switch (action.type) {
    case SET_ALERT:
      return state.concat([action.payload]);

    case REMOVE_ALERT:
      return {
        ...state,
        alerts: state.alerts.filter((alert) => alert.id != action.payload.id),
      };

    default:
      return { ...state };
  }
};
