import React, { useState } from "react";
import { useSelector } from "react-redux";
import profileImage from "../../../resources/images/defaultProfile.png";

const UserDetails = () => {
  const [user, setUser] = useState(null);

  const onChange = (e) => {
    setUser({
      ...user,
      [e.target.name]: [e.target.value],
    });
  };
  const onSubmit = () => {
    if (!isEditable) {
      setIsEditable(!isEditable);
    } else {
      //TODO update user data here
    }
  };
  return (
    <div className="col-md-3 ">
      <div className="row-cols-md-3 text-center">
        <img
          src={profileImage}
          alt="Profile image"
          style={{ width: "60%", height: "70%" }}
          className="border rounded-circle"
        />

        <form onSubmit={onSubmit} style={{ width: "100%" }}>
          <div className="mb-3">
            <label htmlFor="userName" className="form-label">
              User Name
            </label>
            <input
              type="text"
              name="name"
              id="userName"
              value={user.name}
              onChange={onChange}
              disabled={!isEditable ? "disabled" : ""}
              className="form-control"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="userEmail" className="form-label">
              User Email
            </label>
            <input
              type="email"
              name="email"
              disabled={!isEditable ? "disabled" : ""}
              id="userEmail"
              value={user.email}
              onChange={onChange}
              className="form-control"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="userPhone" className="form-label">
              User Phone Number
            </label>
            <input
              type="text"
              disabled={!isEditable ? "disabled" : ""}
              name="phoneno"
              id="userPhone"
              value={user.phoneno}
              onChange={onChange}
              className="form-control"
            />
          </div>

          {/* <button type="submit">{isEditable ? "Update" : "Edit"}</button> */}
        </form>
        <button onClick={onSubmit}>{isEditable ? "Update" : "Edit"}</button>
      </div>
    </div>
  );
};

export default UserDetails;
